import { IOptionsContainer } from './IOptionsContainer.js';
/**
 * Generator capable of producing output based on the provided options and/or
 * the options defined within the instance.
 *
 * @template TOptions The type of options consumed by the generator.
 * @template TResult The type of result produced by the generator.
 */
export interface IGenerator<TOptions, TResult> extends IOptionsContainer<TOptions> {
    /**
     * Generates a result based on the given options. If `opts` is provided, the default behaviour should be overriding
     * the corresponding fields in the instance's options. If the update method is customized
     * in a derived class, the behavior might differ.
     *
     * @param opts - Optional parameters to override the instance's options.
     */
    generate(opts?: Partial<TOptions>): TResult;
}
