/**
 * Abstract class representing a container to store, update and retrieve options.
 *
 * @template T - Type of the options.
 */
export class OptionsContainer {
    get opts() {
        return this._opts;
    }
    set opts(opts) {
        this._opts = opts;
    }
    constructor(opts) {
        this._opts = opts;
    }
    /**
     * Merges provided options and `this.opts`.
     *
     * @param opts - Options to override existing values.
     *
     * @remarks
     * - The options parameter takes priority over values that are part of `this.opts`.
     * - For custom merge behavior, override this method.
     */
    update(opts) {
        this.opts = Object.assign(Object.assign({}, this._opts), opts);
        return this;
    }
}
export default OptionsContainer;
